import React, { useState } from 'react';
import axios from 'axios';
import '../newsformstyles.css'; 

function NewsForm() {
    const [interests, setInterests] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/fetch-news', { interests });
            console.log(response.data);
        } catch (error) {
            console.error('Error fetching news:', error);
        }
    };

    return (
        <div>
            <div><br></br><strong className="center-text">Welcome to p-Newspaper!</strong></div>
            <form onSubmit={handleSubmit} className="form-container">
                <div>
                    <br></br>
                    <label htmlFor="interests">What do you want to read about today?</label>
                    <br></br>
                    <div className="button-container">
                        <textarea
                            id="interests"
                            value={interests}
                            onChange={(e) => setInterests(e.target.value)}
                            placeholder="Enter your interests"
                            className="textarea-input"
                        />
                        <div class="icon" onClick={handleSubmit}>
                          <div class="arrow"></div>
                        </div>
                    </div>
                    <br></br>
                </div>
            </form>
        </div>
    );
}

export default NewsForm;
